#include "Day1.h"

Day1::Day1()
{

}

int Day1::computeFloor()
{
    int upCpt = 0;
    int downCpt = 0;

    upCpt = _input.count("(") ;
    downCpt = _input.count(")") ;

    return (upCpt - downCpt);
}

int Day1::findFirstBasement()
{
    int currentFloor = 0;
    for(int idx = 0 ; idx < _input.size(); idx++)
    {
        if(currentFloor == -1)
            return idx;

        if(_input[idx] == "(")
        {
            currentFloor++;
        }
        else if(_input[idx] == ")")
        {
            currentFloor--;
        }
        else
        {
            fprintf(stdout, "Weird input : %s\r\n", _input[idx].toLatin1());
            fflush(stdout);
        }
    }
    return 0; //error
}

